<?php
class Photo_Contest_Activator {
    public static function activate() {
        global $wpdb;

        $charset_collate = $wpdb->get_charset_collate();

        // Contests table
        $table_contests = $wpdb->prefix . 'photo_contests';
        $sql_contests = "CREATE TABLE IF NOT EXISTS $table_contests (
            id bigint(20) NOT NULL AUTO_INCREMENT,
            title varchar(255) NOT NULL,
            description text,
            start_date datetime NOT NULL,
            end_date datetime NOT NULL,
            status varchar(20) DEFAULT 'draft',
            created_at datetime DEFAULT CURRENT_TIMESTAMP,
            PRIMARY KEY  (id)
        ) $charset_collate;";

        // Submissions table
        $table_submissions = $wpdb->prefix . 'photo_submissions';
        $sql_submissions = "CREATE TABLE IF NOT EXISTS $table_submissions (
            id bigint(20) NOT NULL AUTO_INCREMENT,
            contest_id bigint(20) NOT NULL,
            user_id bigint(20) NOT NULL,
            photo_url varchar(255) NOT NULL,
            title varchar(255) NOT NULL,
            description text,
            votes_count int DEFAULT 0,
            status varchar(20) DEFAULT 'pending',
            created_at datetime DEFAULT CURRENT_TIMESTAMP,
            PRIMARY KEY  (id)
        ) $charset_collate;";

        require_once(ABSPATH . 'wp-admin/includes/upgrade.php');
        dbDelta($sql_contests);
        dbDelta($sql_submissions);
    }
}
