                        .fadeIn()
                        .delay(2000)
                        .fadeOut();
                }
            },
            complete: function() {
                $submitButton.prop('disabled', false);
            }
        });
    });

    // Live preview functionality
    $('.template-preview').on('input', function() {
        const previewContent = $(this).val();
        const previewPane = $(this).siblings('.preview-pane');
        previewPane.html(previewContent);
    });

    // Variable insertion
    $('.insert-variable').on('click', function() {
        const variable = $(this).data('variable');
        const targetId = $(this).closest('.template-section').find('textarea').attr('id');
        const $textarea = $('#' + targetId);

        insertAtCursor($textarea[0], '{' + variable + '}');
    });

    // Helper function to insert text at cursor position
    function insertAtCursor(field, value) {
        if (field.selectionStart || field.selectionStart == '0') {
            const startPos = field.selectionStart;
            const endPos = field.selectionEnd;
            field.value = field.value.substring(0, startPos)
                + value
                + field.value.substring(endPos, field.value.length);
            field.selectionStart = startPos + value.length;
            field.selectionEnd = startPos + value.length;
        } else {
            field.value += value;
        }
        field.focus();
    }
});
