class Photo_Contest_Settings {
    private $defaults;

    public function __construct() {
        require_once PHOTO_CONTEST_PATH . 'includes/class-photo-contest-defaults.php';
        $this->defaults = new Photo_Contest_Defaults();
    }

    public function register_settings() {
        register_setting(
            'photo_contest_email_settings',
            'photo_contest_email_settings',
            array($this, 'sanitize_settings')
        );

        add_settings_section(
            'photo_contest_email_templates',
            __('Email Templates', 'photo-contest'),
            array($this, 'render_templates_section'),
            'photo-contest-settings'
        );
    }

    public function sanitize_settings($input) {
        $sanitized = array();
        $default_templates = Photo_Contest_Defaults::get_default_templates();

        foreach ($input as $key => $value) {
            $sanitized[$key] = wp_kses_post($value);
        }

        // Use defaults for empty fields
        foreach ($default_templates as $key => $default_value) {
            if (empty($sanitized[$key])) {
                $sanitized[$key] = $default_value;
            }
        }

        return $sanitized;
    }

    public function render_templates_section() {
        echo '<p>' . __('Configure your email templates below. Use the available variables in your templates.', 'photo-contest') . '</p>';
    }
}
